    // Happy path for not maintaining spacing
    @Test
    public void testRemoveNthCharacter1() {
        manipulatedstring.setString("I'd b3tt3r put s0me d161ts in this 5tr1n6, right?");
        assertEquals("I' bttr uts0e 16tsinths trn6 rgh?", manipulatedstring.removeNthCharacter(3, false));
    }

    // Happy path for maintaining spacing
    @Test
    public void testRemoveNthCharacter2() {
        manipulatedstring.setString("I'd b3tt3r put s0me d161ts in this 5tr1n6, right?");
        assertEquals("I'  b tt r  ut s0 e  16 ts in th s  tr n6  r gh ?", manipulatedstring.removeNthCharacter(3, true));
    }

    // This test checks removing no characters
    @Test
    public void testRemoveNthCharacter3() {
        manipulatedstring.setString("I'd b3tt3r put s0me d161ts in this 5tr1n6, right?");
        assertThrows(IllegalArgumentException.class, () -> manipulatedstring.removeNthCharacter(0, true));
    }

    // This test checks removing all of the characters, but maintaining whitespace
    @Test
    public void testRemoveNthCharacter4() {
        manipulatedstring.setString("I'd b3tt3r put s0me d161ts in this 5tr1n6, right?");
        assertEquals("                                                 ", manipulatedstring.removeNthCharacter(1, true));
    }

    // This test checks removing all of the characters and not maintaining whitespace
    @Test
    public void testRemoveNthCharacter5() {
        manipulatedstring.setString("I'd b3tt3r put s0me d161ts in this 5tr1n6, right?");
        assertEquals("", manipulatedstring.removeNthCharacter(1, false));
    }

    // This test checks removing an Nth character that is larger than the string
    @Test
    public void testRemoveNthCharacter6() {
        manipulatedstring.setString("I'd b3tt3r put s0me d161ts in this 5tr1n6, right?");
        assertThrows(IndexOutOfBoundsException.class, () -> manipulatedstring.removeNthCharacter(100, false));
    }

    // This test checks removing a negative Nth character
    @Test
    public void testRemoveNthCharacter7() {
        manipulatedstring.setString("I'd b3tt3r put s0me d161ts in this 5tr1n6, right?");
        assertThrows(IllegalArgumentException.class, () -> manipulatedstring.removeNthCharacter(-1, false));
    }

    // Happy path
    @Test
    public void testGeSubStrings1() {
        manipulatedstring.setString("This is my string");
        String [] sStings = manipulatedstring.getSubStrings(3, 4);

        assertEquals(sStings[0], "my");
        assertEquals(sStings[1], "string");
    }

    // This test tries to get substrings with a starting value less than 0
    @Test
    public void testGeSubStrings2() {
        manipulatedstring.setString("This is my string");
        assertThrows(IllegalArgumentException.class, () -> manipulatedstring.getSubStrings(-1, 4));
    }

    // This test tries to get substrings with the starting value greater than the ending one
    @Test
    public void testGeSubStrings3() {
        manipulatedstring.setString("This is my string");
        assertThrows(IllegalArgumentException.class, () -> manipulatedstring.getSubStrings(2, 1));
    }

    // This test tries to get substrings with both the starting and ending value negative
    @Test
    public void testGeSubStrings4() {
        manipulatedstring.setString("This is my string");
        assertThrows(IllegalArgumentException.class, () -> manipulatedstring.getSubStrings(-2, -1));
    }

    // This test tries to get substrings with an ending value larger than the string itself
    @Test
    public void testGeSubStrings5() {
        manipulatedstring.setString("This is my string");
        assertThrows(IndexOutOfBoundsException.class, () -> manipulatedstring.getSubStrings(1, 100));
    }

    // This test checks getting substrings when the starting and ending value are the same
    @Test
    public void testGeSubStrings6() {
        manipulatedstring.setString("This is my string");
        String [] sStings = manipulatedstring.getSubStrings(1, 1);

        assertEquals(sStings.length, 1);
        assertEquals(sStings[0], "This");
    }

    // Happy path
    @Test
    public void testRestoreString1()
    {
        manipulatedstring.setString("art");
        int [] array;
        array=new int[]{1,0,2};
        String restoreString = manipulatedstring.restoreString(array);
        assertEquals(restoreString, "rat");
    }

    // Tests that case is maintained
    @Test
    public void testRestoreString2()
    {
        manipulatedstring.setString("AaBbCc");
        int [] array;
        array=new int[]{1,0,3,2,5,4};
        String restoreString = manipulatedstring.restoreString(array);
        assertEquals(restoreString, "aAbBcC");
    }

    // Tests when the indicies array size is smaller than the string length
    @Test
    public void testRestoreString3()
    {
        manipulatedstring.setString("AaBbCc");
        int [] array;
        array=new int[]{1};
        assertThrows(IllegalArgumentException.class, () -> manipulatedstring.restoreString(array));
    }

    // Tests when the indicies array size is larger than the string length
    @Test
    public void testRestoreString4()
    {
        manipulatedstring.setString("AaBbCc");
        int [] array;
        array=new int[]{1,2,3,4,5,6,7};
        assertThrows(IllegalArgumentException.class, () -> manipulatedstring.restoreString(array));
    }

    // Tests when an index is less than zero
    @Test
    public void testRestoreString5()
    {
        manipulatedstring.setString("AaBbCc");
        int [] array;
        array=new int[]{1,2,3,4,5,-1};
        assertThrows(IndexOutOfBoundsException.class, () -> manipulatedstring.restoreString(array));
    }

    // Tests when an index is larger than the string length
    @Test
    public void testRestoreString6()
    {
        manipulatedstring.setString("AaBbCc");
        int [] array;
        array=new int[]{1,2,3,4,5,7};
        assertThrows(IndexOutOfBoundsException.class, () -> manipulatedstring.restoreString(array));
    }
